			<label for="code">Enter the code send to your phone:</label>
			<input
				type="code"
				name="code"
				value={code}
				placeholder={"123456"}
				onChange={codeHandler}
			/>
			<button>Submit</button>
		</form>
	);
};

export default FormComponent;
